public function explodeBitCfg($bit): array
{
  $c = $this->class_cfg['arch']['user_options_bits'];
  if (!empty($bit[$c['cfg']])
    && ($cfg = json_decode($bit[$c['cfg']], true))
  ) {
    foreach ($cfg as $i => $v) {
