function($obj, array $prop_path, $check_empty = false)
  {
      array $prop_path,
      bool $check_empty = false
  ): ?bool
  {
    $o =& $obj;
    foreach ($prop_path as $p) {
      if (is_array($o)) {
        if (!\array_key_exists($p, $o)) {
          return false;
        }

        if ($check_empty && !$o[$p]) {
          return false;
        }

        $o =& $o[$p];
      }
      elseif (\is_object($o)) {
        if (!\property_exists($o, $p)) {
          return false;
        }

        if ($check_empty && !$o->$p) {
          return false;
        }

        $o =& $o->$p;
      }
      else {
        return false;
      }
    }

    return true;
  }

