function($path, $format = 'Y/m/d', $max = 100, bbn\File\System $fs = NULL)
  {
      $format = 'Y/m/d',
      $max = 100,
      File\System $fs = null
  ): ?string
  {
    if (empty($format)) {
      $format = 'Y/m/d';
    }

    if (!$max) {
      $max = 100;
    }

    if (!$fs) {
      $fs = new File\System();
    }

    // One dir per $format
    $spath = date($format);
    if ($spath) {
      $path = $fs->createPath($path.(substr($path, -1) === '/' ? '' : '/').$spath);
      if ($fs->isDir($path)) {
        $num = count($fs->getDirs($path));
        if ($num) {
          // Dir or files
          $num_files = count($fs->getFiles($path.'/'.$num, true));
          if ($num_files >= $max) {
            $num++;
          }
        }
        else {
          $num = 1;
        }

        if ($fs->createPath($path.'/'.$num)) {
          return $path.'/'.$num.'/';
        }
      }
    }

    return null;
  }

cleanStoragePath.code:
function($path, $format = 'Y/m/d', bbn\File\System $fs = NULL)
  {
      $format = 'Y/m/d',
      File\System $fs = null
  ): ?int
  {
    if (empty($format)) {
      $format = 'Y/m/d';
    }

    if (!$fs) {
      $fs = new File\System();
    }

    if (!$fs->isDir($path)) {
      return null;
    }

    $limit = count(self::split($format, '/')) + 1;
    $res   = 0;
    while ($limit > 0) {
      if (!$fs->getNumFiles($path) && $fs->delete($path)) {
        $limit--;
        $res++;
        $path = dirname($path);
      }
      else{
        break;
      }
    }

    return $res;
  }

