function(array &$ar, $key, $dir = '')
  {
    $args = \func_get_args();
    array_shift($args);
    if (\is_string($key)) {
      $args = [[
        'key' => $key,
        'dir' => $dir
      ]];
    }

    usort(
      $ar,
      function ($a, $b) use ($args) {
        foreach ($args as $arg) {
          $key = $arg['key'];
          $dir = $arg['dir'] ?? 'asc';
          if (!\is_array($key)) {
            $key = [$key];
          }

          $v1 = self::pick($a, $key);
          $v2 = self::pick($b, $key);
          $a1 = strtolower($dir) === 'desc' ? ($v2 ?? null) : ($v1 ?? null);
          $a2 = strtolower($dir) === 'desc' ? ($v1 ?? null) : ($v2 ?? null);
          if (!Str::isNumber($v1, $v2)) {
            $a1  = str_replace('.', '0', str_replace('_', '1', Str::changeCase($a1, 'lower')));
