function(array $data, $delimiter = ';', $enclosure = '"', $separator = '
', $encloseAll = false, $nullToMysqlNull = false)
  {
    $delimiter_esc = preg_quote($delimiter, '/');
    $enclosure_esc = preg_quote($enclosure, '/');

    $lines = [];
    foreach ($data as $d) {
      $output = [];
      foreach ($d as $field) {
        if ($field === null && $nullToMysqlNull) {
          $output[] = 'NULL';
          continue;
        }

        // Enclose fields containing $delimiter, $enclosure or whitespace
        if ($encloseAll || preg_match("/(?:${delimiter_esc}|${enclosure_esc}|\s)/", $field)) {
          $output[] = $enclosure.str_replace($enclosure, '\\'.$enclosure, $field) . $enclosure;
        }
        else {
          $output[] = $field;
        }
      }

      $lines[] = implode($delimiter, $output);
    }

    return self::join($lines, $separator);
  }

