function($subject, $search, $start = NULL)
  {
    $res = false;
    if (is_array($subject)) {
      $i = count($subject) - 1;
      if ($i) {
        if ($start > 0) {
          if ($start > $i) {
            return -1;
          }

          $i = $start;
        }
        elseif ($start < 0) {
          $i -= $start;
          if ($i < 0) {
            return -1;
          }
        }

        foreach ($subject as $s) {
          if (($i <= $start) && ($s === $search)) {
            $res = $i;
            break;
          }
          else{
            $i--;
          }
        }
      }
    }
    elseif (is_string($subject)) {
      if ($start > 0) {
        $start = strlen($subject) - (strlen($subject) - $start);
      }

      $res = strrpos($subject, $search, $start);
    }

    return $res === false ? -1 : $res;
  }

