set_date.code:
function($date)
  {
    // Sets the current date
    if ( !bbn\str::is_number($date) && !($date = strtotime($date)) ){
      return;
    }
    $t = time();
    // Impossible to write history in the future
    if ( $date > $t ){
      $date = $t;
    }
    self::$date = $date;
  }

