get_history.code:
function($table, $id, $col = '')
  {
      self::check($table) && 
      ($modelize = self::get_table_cfg($table))
    ){
      $pat = [
        'ins' => 'INSERT',
        'upd' => 'UPDATE',
        'res' => 'RESTORE',
        'del' => 'DELETE'
      ];
      $r = [];
      $fields = [
          'date' => 'tst',
          'user' => 'usr',
          'col'
        ];
      $where = [
        'uid' => $id
      ];
      if ( !empty($col) ){
        if ( !\bbn\str::is_uid($col) ){
          $fields[] = $modelize['fields'][$col]['type'] === 'binary' ? 'ref' : 'val';
          $col = self::$databases_obj->column_id($col, $table);
        }
        else {
          $idx = \bbn\x::find($modelize['fields'], ['id_option' => strtolower($col)]);
          if (null === $idx) {
            throw new \Error("Impossible to find the option $col");
          }
          $fields[] = $modelize['fields'][$idx]['type'] === 'binary' ? 'ref' : 'val';
        }
        $where['col'] = $col;
      }
      else {
        $fields[] = 'val';
        $fields[] = 'ref';
      }
      foreach ( $pat as $k => $p ){
        $where['opr'] = $p;
        if ( $q = self::$db->rselect_all([
          'table' => self::$table,
          'fields' => $fields,
          'where' => [
            'conditions' => $where
          ],
          'order' => [[
            'field' => 'tst',
            'dir' => 'desc'
          ]]
        ]) ){
          $r[$k] = $q;
        }
      }
      return $r;
    }
  }

