    foreach ( $args as $ele ){
      if ( !isset($this->content) ){
        if ( \is_array($ele) && isset($ele[0]) ){
          $this->content = $ele;
        }
        else{
          $this->content = \is_object($ele) ? [$ele] : $ele;
        }
      }
      else if ( \is_array($this->content) ){
        if ( \is_array($ele) ){
          array_merge($this->content, $ele);
        }
        else{
          array_push($this->content, $ele);
        }
      }
      else if ( \is_string($this->content) ){
